mapper.update:
UPDATE 
SHOP_CART SET
SCT_COUNT = #{sct_count}
WHERE SCT_NO = #{sct_no}

mapper.select:
SELECT 
	COUNT(SCT_NO) 
FROM SHOP_CART
WHERE SCT_MEM_NO = #{sct_mem_no}
AND SCT_PRO_NO = #{sct_pro_no}

mapper.update:
UPDATE 
SHOP_CART SET
	SCT_COUNT = SCT_COUNT + #{sct_count}
WHERE SCT_MEM_NO = #{sct_mem_no}
AND SCT_PRO_NO = #{sct_pro_no}

mapper.insert:
INSERT 
INTO ORDERS 
VALUES
(
	#{odr_no_max}
	, #{mem_no}
	, #{useMileage}
	, #{s_totPrice} - #{useMileage} + 2500
	, #{odr_way}
	, sysdate()
	, null
	, null
	, #{odr_condition}
)

INSERT 
INTO ODR_DET 
VALUES
(
	(SELECT IFNULL(MAX(ODT_NO), 0)+1 FROM ODR_DET a)
	, #{s_pro_no}
	, #{s_odt_count}
	, #{s_pro_disprice}
	, #{s_pro_disprice}
	, #{s_pro_disprice}
	, #{s_pro_milege}
	, '1' 
	, #{odr_no_max}
	, #{del_no_max}
)

mapper.update:
UPDATE 
MEM_COM SET
	MEM_MIL = MEM_MIL + #{s_totMilege}
WHERE MEM_NO = #{mem_no}

UPDATE 
PRODUCT SET
PRO_STOCKCOUNT = PRO_STOCKCOUNT - #{s_count}
WHERE PRO_NO = #{s_pro_no}

mapper.select:
SELECT SQL_CALC_FOUND_ROWS * FROM ( SELECT t1.* , @ROWNUM := @ROWNUM + 1 AS rm , -999 AS tot_cont FROM ( select ODR_NO , ODR_MEM_NO , M.MEM_ID , ODR_MILEAGE , ODR_AMOUNT , ODR_WAY , DATE_FORMAT(ODR_ODATE, '%Y-%c-%d') AS ODR_ODATE , IFNULL(DATE_FORMAT(odr_sdate, '%Y-%c-%d'), '입금미확인') AS ODR_SDATE , IFNULL(DATE_FORMAT(odr_cdate, '%Y-%c-%d'), '미정') AS ODR_CDATE , ODR_CONDITION , (CASE O.ODR_CONDITION WHEN 1 THEN '주문신청' WHEN 2 THEN '결제확인' WHEN 3 THEN '배송' WHEN 4 THEN '주문완료' END) AS ODR_CONDITION2 FROM ORDERS O, MEM_COM M WHERE O.ODR_MEM_NO = M.MEM_NO
AND ODR_MEM_NO = #{ord_mem_no}
ORDER BY ODR_NO DESC ) t1, (select @ROWNUM := 0) A ) t2 LIMIT #{begin_ms},#{numPerPage}

mapper.update:
UPDATE 
ORDERS SET 
	ODR_CONDITION = #{odr_condition}
	
	, ODR_SDATE = sysdate()
	
	
	, ODR_CDATE = sysdate()
	
WHERE ODR_NO = #{odr_no}
