mapper.update:
UPDATE 
VIR_BILL SET
	VBL_TITLE = #{vbl_title}
	, VBL_BOR_ANSWER = #{vbl_bor_answer}
WHERE VBL_NO = #{vbl_no}

mapper.select:
SELECT t2.* FROM ( SELECT t1.* , ROWNUM rm , COUNT(VBB_NO) OVER() AS tot_cont FROM ( SELECT VBB.VBB_NO, VBB.VBB_CONTENT, MEM.MEM_ID, TO_CHAR(VBB.VBB_DATE, 'yyyy-mm-dd') AS VBB_DATE, VBB.VBB_RECOMM, VBB.VBB_COUNT, VBB.VBB_BTR_ANSWER, VBB.VBB_TITLE FROM VIR_BILL_BOARD VBB, MEM_COM MEM WHERE VBB.VBB_MEM_NO = MEM.MEM_NO
AND VBB.VBB_MEM_NO = #{vbb_mem_no}
ORDER BY VBB.VBB_NO DESC ) t1 ) t2 WHERE rm BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE 
VBB_REPLY SET
	VBR_CONTENT = #{vbr_content}
WHERE VBR_NO = #{vbr_no}

mapper.insert:
INSERT 
INTO VBJ_DET
(
	VBD_NO
	,VBD_QUANTITY
	,VBD_VBJ_NO
	,VBD_PRO_NO
)
VALUES
(
	(SELECT NVL(MAX(VBD_NO),0)+1 FROM VBJ_DET)
	,#{vbd_quantity}
	,#{vbj_no}
	,#{vbd_pro_no}
)
